  public static linkLibrary(libraryName: string, address: string): void {
    const bytecode = PolarisContract.BYTECODE;
    const cleanedAddress = address.replace('0x', '');
    // truncate to 37 characters
    const truncatedName = libraryName.slice(0, 36);
    const libLabel = '__' + truncatedName + Array(37 - truncatedName.length).join('_') + '__';
    PolarisContract.BYTECODE = bytecode.replace(new RegExp(libLabel, 'g'), cleanedAddress);
  }
  public static async deployAsync(
    provider: Provider,
    txDefaults: Partial<TxData>,
    _uniswap: string,
  ): Promise<PolarisContract> {
    const abi = JSON.parse(PolarisContract.ABI);
    const bytecode = PolarisContract.BYTECODE;

    return PolarisContract.deployWithBytecodeAndAbiAsync(bytecode, abi, provider, txDefaults, _uniswap);
  }
  public static async deployFromArtifactAsync(
    artifact: any,
    provider: Provider,
    txDefaults: Partial<TxData>,
    _uniswap: string,
  ): Promise<PolarisContract> {
    let abi;
    let bytecode;
    if (!_.isUndefined(artifact.abi)) {
      abi = artifact.abi; // Truffle artifact
      bytecode = artifact.bytecode;
    } else if (!_.isUndefined(artifact.compilerOutput.abi)) {
      abi = artifact.compilerOutput.abi; // 0x artifact
      bytecode = artifact.compilerOutput.evm.bytecode.object;
    }

    if (_.isUndefined(abi) || _.isUndefined(bytecode)) {
      throw new Error('Compiler output not found in the artifact file');
    }
    return PolarisContract.deployWithBytecodeAndAbiAsync(bytecode, abi, provider, txDefaults, _uniswap);
  }
  public static async deployWithBytecodeAndAbiAsync(
    bytecode: string,
    abi: ContractAbi,
    provider: Provider,
    txDefaults: Partial<TxData>,
    _uniswap: string,
  ): Promise<PolarisContract> {
    const constructorAbi = BaseContract._lookupConstructorAbi(abi);
    [_uniswap] = BaseContract._formatABIDataItemList(
      constructorAbi.inputs,
      [_uniswap],
      BaseContract._bigNumberToString,
    );
    const iface = new ethers.utils.Interface(abi);
    const deployInfo = iface.deployFunction;
    const txData = deployInfo.encode(bytecode, [_uniswap]);
    const web3Wrapper = new Web3Wrapper(provider);
    const txDataWithDefaults = await BaseContract._applyDefaultsToTxDataAsync(
      { data: txData },
      txDefaults,
      web3Wrapper.estimateGasAsync.bind(web3Wrapper),
    );
    const txHash = await web3Wrapper.sendTransactionAsync(txDataWithDefaults);
    logUtils.log(`transactionHash: ${txHash}`);
    const txReceipt = await web3Wrapper.awaitTransactionSuccessAsync(txHash);
    logUtils.log(`Polaris successfully deployed at ${txReceipt.contractAddress} with gas: ${txReceipt.gasUsed}`);
    const contractInstance = new PolarisContract(abi, txReceipt.contractAddress as string, provider, txDefaults);
    contractInstance.constructorArgs = [_uniswap];
    PolarisContract.BYTECODE = PolarisContract.ORIGINAL_BYTECODE;
    return contractInstance;
  }
  public static new(address: string, provider: Provider, txDefaults?: Partial<TxData>): PolarisContract {
    const abi = JSON.parse(PolarisContract.ABI);
    const contractInstance = new PolarisContract(abi, address, provider, txDefaults);
    return contractInstance;
  }
  constructor(abi: ContractAbi, address: string, provider: Provider, txDefaults?: Partial<TxData>) {
    super('Polaris', abi, address, provider, txDefaults);
    classUtils.bindAll(this, ['_ethersInterfacesByFunctionSignature', 'address', 'abi', '_web3Wrapper']);
  }
